using UnityEngine;using UnityEditor;/*** Author: Nevin Foster* Editor Functions, used to run function calls within the editor.*/public class EditModeFunctions : EditorWindow{    [Tooltip("I am a Tip!")]    GameObject go = null;    Transform parent = null;    int arg_int;        [MenuItem("Window/Edit Mode Functions")]    public static void ShowWindow()    {        GetWindow<EditModeFunctions>("Edit Mode Functions");    }    private void OnGUI()    {        GUILayout.Label("I Am A Tip! ");        EditorGUILayout.BeginHorizontal();        GUILayout.Label("Prefab ");        go = (GameObject) EditorGUILayout.ObjectField(go, typeof(GameObject), true);        EditorGUILayout.EndHorizontal();        EditorGUILayout.BeginHorizontal();        GUILayout.Label("Parent ");        parent = (Transform)EditorGUILayout.ObjectField(parent, typeof(Transform), true);        EditorGUILayout.EndHorizontal();        EditorGUILayout.BeginHorizontal();        GUILayout.Label("Number of objects ");        arg_int = EditorGUILayout.IntField(arg_int);        EditorGUILayout.EndHorizontal();        EditorGUILayout.Space();        GUILayout.Label("Creates objects between two points");        if (GUILayout.Button("Array Fill"))        {            Transform[] selectedTransforms = Selection.transforms;            if (selectedTransforms.Length != 2)            {                Debug.LogWarning("Can only call array fill with two object selected in scene. Number of objects currently: " + selectedTransforms.Length);            }            else            {                Transform selected1 = selectedTransforms[0];                Transform selected2 = selectedTransforms[1];                if (selected1 == null || selected2 == null)                {                    Debug.LogWarning("Cannot call array fill without two object selected in scene.");                }                else if (arg_int <= 0 || arg_int > 99)                {                    Debug.LogWarning("\"Number of Objects\" is out of bounds: " + arg_int);}                else if (go == null)                {                    Debug.LogWarning("Prefab object not set!");                }                else                    ArrayFill(selected2, selected1, go, arg_int, parent);            }        }    }    private void ArrayFill(Transform selected1, Transform selected2, GameObject prefab, int number, Transform parent)    {        Debug.Log("Fuction Called!! Object1: " + selected1.name + "   Object2: " + selected2.name);        if (parent == null)        {            parent = selected1.parent;        }        for (int i = 0; i < number; i++)        {            GameObject spawn = (GameObject) PrefabUtility.InstantiatePrefab(prefab, parent);            spawn.name += " (spawn " + i + ")";            spawn.transform.position = selected1.position + (i + 1) * ((selected2.position - selected1.position) / (number + 1));            spawn.transform.localEulerAngles = selected1.localEulerAngles + (i + 1) * ((selected2.localEulerAngles - selected1.localEulerAngles) / (number + 1));        }    }}